import './TodoDescription.css';
import {useEffect, useState} from "react";

export function TodoDescription ({currentTodo, setUpdatedTodo}){
    const [newTodo, setNewTodo] = useState({})
    const [isNew, setIsNew] = useState(false)

    useEffect(() => {
        setNewTodo(currentTodo)
    }, [currentTodo])

    const handleChangeDescription = (e) => {
        setNewTodo({...currentTodo, [e.target.name]: e.target.value})
    }

    const saveChange = () => {
        setNewTodo({...newTodo, history: [...newTodo.history, {field: 'description', action: 'change', prevValue: currentTodo.description, currentValue: newTodo.description, appliedAt: Date.now()}]})
        setIsNew(true)
    }

    useEffect(() => {
        if (isNew) setUpdatedTodo(newTodo)
        setIsNew(false)
    }, [isNew])

    return(
        <>
            {currentTodo.title ?
                <div className={'descriptionContainer'}>
                    <h1>Title</h1>
                    <h2>
                        {newTodo.title}
                    </h2>
                    <textarea onChange={handleChangeDescription} value={newTodo.description} name="description" id="" cols="30" rows="10"/>
                <button onClick={saveChange}>Save</button>

                    <div className={'history'}>
                        <h3>{newTodo.description}</h3>
                    </div>

                </div>
                :
                <div>
                    Todos not found
                </div>
            }
        </>
    )

}
