import {Component} from "react";
import './TodoItem.css'

export function TodoItem(props) {

    return (
        <div className='todoItem' onClick={() => props.getCurrentTodo(props.item)}>
            <input type="checkbox" onClick={() => props.onItemComplete(props.item.id)}></input>
            <span className={props.item.completed ? 'completed' : 'default'}>{props.item.title}</span>
            <div className='description'>
                <span
                    className={props.item.completed ? 'completed' : 'default'}>{props.item.description}</span>
            </div>

            {/*<button onClick={() => this.props.onItemDelete(this.props.item.id)}>X</button>*/}
        </div>
    );
}
