import './TodoDescription.css';
import {useEffect, useState} from "react";

export function TodoDescription ({currentTodo}){
    // const [active, setActive] = useState(false);

    const [updatedTodo, setUpdatedTodo] = useState(currentTodo)

    useEffect(() => {
        setUpdatedTodo(currentTodo)
    }, [currentTodo])

    const handleChangeDescription = (e) => {
        setUpdatedTodo(prevState => {
           return  {...prevState, [e.target.name]: e.target.value
            }
        })
    }

    const saveChange = (e) => {
        setUpdatedTodo(prev=>{
            return {...prev, [e.target.description]: e.target.value
            }
        })
    }

    return(
        <>
            {currentTodo.title ?
                <div className={'descriptionContainer'}>
                    <h1>Title</h1>
                    <h2>
                        {updatedTodo.title}
                    </h2>
                    <textarea onChange={handleChangeDescription} value={updatedTodo.description} name="description" id="" cols="30" rows="10"/>
                <button onClick={saveChange}>Save</button>
                </div>
                :
                <div>
                    Todos not found
                </div>
            }
        </>
    )

}
