import {Component} from "react";
// import {TodoItem} from "../LeftSidebar/TodoItem";
import './TodoDescription.css';

export class TodoDescription extends Component {

    constructor(props) {
        super(props);
        this.currentTodo = props.currentTodo
    }

    render() {
        return (
            <div className={'descriptionContainer'}>
                {!this.currentTodo ?
                    <h1>Title</h1>
                    : <h1>
                        {this.currentTodo.title}
                    </h1>}
                <p className={'todoDescription'}>{this.currentTodo.description}</p>
            </div>
        )
    }
}
