import {Component} from "react";
// import {TodoItem} from "../LeftSidebar/TodoItem";
import './TodoDescription.css';

export class TodoDescription extends Component {

    constructor(props) {
        super(props);
    }
    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log(this.props)
    }

    render() {
        return (
            <div className={'descriptionContainer'}>
                {
                    this.props.currentTodo.title ?
                        // <>
                            <form>
                            <h2>

                                {this.props.currentTodo.title}
                            </h2>
                            <textarea className={'todoDescription'}>{this.props.currentTodo.description}</textarea>
                            </form>
                        // </>
                         :
                         <h1>Title</h1>
                }
            </div>
        )
    }
}
