import './TodoDescription.css';
import {useEffect, useState} from "react";

export function TodoDescription ({currentTodo, setUpdatedTodo}){
    const [newTodo, setNewTodo] = useState({})
    const [isNew, setIsNew] = useState(false)

    useEffect(() => {
        setNewTodo(currentTodo)
    }, [currentTodo])

    const handleChangeDescription = (e) => {
        setNewTodo({...currentTodo, [e.target.name]: e.target.value})
    }

    const saveChange = () => {
        setNewTodo({...newTodo, history: [...newTodo.history, {field: 'description', action: 'change', prevValue: currentTodo.description, currentValue: newTodo.description, appliedAt: Date.now().toLocaleTimeString().slice(0,-3)}]})
        setIsNew(true)
    }

    useEffect(() => {
        if (isNew) setUpdatedTodo(newTodo)
        setIsNew(false)
    }, [isNew])

    return(
        <>
            {currentTodo.title ?
                <div className={'mainAreaContainer'}>
                <div className={'descriptionContainer'}>
                    <h1>Title</h1>
                    <h2>
                        {newTodo.title}
                    </h2>
                    <textarea onChange={handleChangeDescription} value={newTodo.description} name="description" id="" cols="60" rows="10"/>
                <button onClick={saveChange}>Save</button>
                </div>

                    <div>
                        <h1>History</h1>
                        <div className={'history'}>

                            <span><b>{'todo-description: '}</b>{currentTodo.title}</span><br />
                            <span><b>{'previous todo-item: '}</b>{currentTodo.description}</span>
                        </div>
                    </div>

                </div>

                :
                <div>
                    Todos not found
                </div>
            }
        </>
    )

}
