import {Component} from "react";
import {TodoForm} from "./components/leftSidebar/TodoForm";
import {TodoDescription} from "./components/mainArea/TodoDescription";
import './components/Todo.css'

export class Todo extends Component{

    state = {
        list: [
            {id:1, title: 'Clean the flat', completed: false, description: 'прибраться в комнате получше'},
            {id:2, title: 'Garbage', completed: false, description: ''},
            {id:3, title: 'Prepare dinner', completed: false, description: ''}
        ]}

    getCurrentTodo = (todo) => {
        this.setState(todo)
    }

    onTodoAddHandler = (newItem) => {
        console.log(newItem);
    }

    componentDidUpdate(prevProps, prevState, snapshot) {
        console.log(123)
    }

    render() {
        return(
            <>
                <div className={'todo'}>
                    <TodoForm
                    onTodoAdd={this.onTodoAddHandler}
                    getCurrentTodo={this.getCurrentTodo}
                    />
                    <TodoDescription currentTodo={this.state} />
                </div>

            </>
        )
    }
}
