import './TodoDescription.css';
import {useState} from "react";

export function TodoDescription ({currentTodo}){
    const [active, setActive] = useState(false);
    return(
        <>
            {currentTodo ?
                <div className={'descriptionContainer'}>
                    <h1>Title</h1>
                    <h2 >
                        {active ? active.currentTodo.title : null}
                    </h2>
                    <p
                        className={'todoDescription'}>
                        {active ? active.currentTodo.description : null}
                    </p>
                </div>
                :
                <div>
                    Todo not found
                </div>
            }
        </>
    )

}
