    const [currentTodo, setCurrentTodo] = useState({})

    const [name, setName] = useState('')

    const handleChangeItem = (name) => {
        setName(name)
    }

    useEffect(() => {
        console.log(currentTodo)
    }, [currentTodo])

        return(
            <>
                <div className={'todo'}>
                    <TodoForm setCurrentTodo={setCurrentTodo}/>
                    <TodoDescription currentTodo={currentTodo}
                    change={handleChangeItem}/>
                </div>

            </>
        )
}
